// AI-Powered Commitment Matching Logic
const invoice = $input.first().json;
const commitments = $node['Get Procore Commitments'].json;

// Fuzzy vendor name matching
function similarity(s1, s2) {
  const longer = s1.length > s2.length ? s1 : s2;
  const shorter = s1.length > s2.length ? s2 : s1;
  if (longer.length === 0) return 1.0;
  const costs = [];
  for (let i = 0; i <= shorter.length; i++) {
    let lastValue = i;
    for (let j = 0; j <= longer.length; j++) {
      if (i === 0) { costs[j] = j; }
      else if (j > 0) {
        let newValue = costs[j - 1];
        if (shorter.charAt(i - 1) !== longer.charAt(j - 1)) {
          newValue = Math.min(Math.min(newValue, lastValue), costs[j]) + 1;
        }
        costs[j - 1] = lastValue;
        lastValue = newValue;
      }
    }
    if (i > 0) costs[longer.length] = lastValue;
  }
  return (longer.length - costs[longer.length]) / longer.length;
}

let bestMatch = null;
let bestScore = 0;

for (const commitment of commitments) {
  const vendorSim = similarity(
    invoice.normalized_vendor,
    (commitment.vendor_name || '').toLowerCase().replace(/[^a-z0-9]/g, '')
  );

  const remaining = commitment.grand_total - (commitment.invoiced_amount || 0);
  const amountFits = invoice.amount <= remaining * 1.05; // 5% tolerance

  const score = vendorSim * (amountFits ? 1 : 0.5);

  if (score > bestScore) {
    bestScore = score;
    bestMatch = {
      commitment_id: commitment.id,
      commitment_number: commitment.number,
      vendor_name: commitment.vendor_name,
      committed_amount: commitment.grand_total,
      billed_to_date: commitment.invoiced_amount || 0,
      remaining: remaining,
      vendor_similarity: (vendorSim * 100).toFixed(1),
      amount_fits: amountFits,
      match_score: (score * 100).toFixed(1)
    };
  }
}

let confidence = 'LOW';
if (bestScore > 0.9) confidence = 'HIGH';
else if (bestScore > 0.6) confidence = 'MEDIUM';

return [{
  json: {
    ...invoice,
    match: bestMatch,
    match_confidence: confidence,
    match_score: bestMatch ? bestMatch.match_score : 0
  }
}];
